import './App.css';
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import Turtle from './components/Turtle';
import Learning from './components/Learning';
import Bookshelf from './components/Bookshelf';
import Home from './components/Home';
import Recommend from './components/Recommend';
import Login from './components/Login'; // 네비게이션 바가 보이지 않는 페이지

// 공통적인 List 컴포넌트
function List(props) {
  return (
    <li className={props.thing}>
      <Link to={props.route}>{props.name}</Link>
    </li>
  );
}

// 로고 컴포넌트
function Logo() {
  return (
    <li className='logo'>
      <Link to='/Home'>Logo</Link>
    </li>
  );
}

export default function App() {
  // 현재 경로 확인
  const location = useLocation();

  return (
    <div>
      <Router>
        <div>
          {/* 특정 경로에서는 네비게이션 바 숨기기 */}
          {location.pathname !== '/Learning' && (
            <nav>
              <ul className='navAll'>
                <Logo />
                <List route='/Bookshelf' name='내 서재' thing='bookshelf' />
                <List route='/Turtle' name='거북이 수족관' thing='turtle' />
                <List route='/Recommend' name='책 추천받기' thing='recommend' />
                <List route='/Learning' name='독서 시작하기' thing='learning' />
              </ul>
            </nav>
          )}

          {/* 페이지별 라우팅 설정 */}
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/Home" element={<Home />} />
            <Route path="/Bookshelf" element={<Bookshelf />} />
            <Route path="/Turtle" element={<Turtle />} />
            <Route path="/Recommend" element={<Recommend />} />
            <Route path="/Learning" element={<Learning />} />
            <Route path="/login" element={<Login />} /> {/* 로그인 페이지는 네비게이션 바 없음 */}
          </Routes>
        </div>
      </Router>
    </div>
  );
}
