import './css/Learning.css';
import React, { useState } from 'react';
import { Document, Page, pdfjs } from 'react-pdf';

pdfjs.GlobalWorkerOptions.workerSrc = '/pdf.worker.mjs';

// Button Component
function Button({ name, src, alt, onClick }) {
    return (
        <img
            src={src}
            className={name}
            alt={alt}
            onClick={onClick}
        />
    );
}

// Reading Component
function Reading({ onComprehensionTrigger }) {
    const [pageNumber, setPageNumber] = useState(1); // 현재 페이지 상태
    const [numPages, setNumPages] = useState(null); // 전체 페이지 수

    const onDocumentLoadSuccess = ({ numPages }) => {
        setNumPages(numPages);
    };

    const goToPrevPage = () => {
        if (pageNumber > 2) {
            setPageNumber(pageNumber - 2); // 두 페이지씩 넘김
        }
    };

    const goToNextPage = () => {
        if (pageNumber + 1 < numPages) {
            setPageNumber(pageNumber + 2); // 두 페이지씩 넘김
        }
        // 4페이지 도달 시 comprehension 트리거
        if (pageNumber >= 4) {
            onComprehensionTrigger();
        }
    };

    return (
        <>
            <Button
                name='Learning_Button_Left'
                src='/Left.png'
                alt="Left navigation button"
                onClick={goToPrevPage} // 왼쪽 버튼 클릭 시 두 페이지 감소
            />
            <div className='Learning_Book_Left'>
                <Document
                    file='/Hamlet.pdf'
                    onLoadSuccess={onDocumentLoadSuccess}
                >
                    <Page pageNumber={pageNumber} renderTextLayer={false}/>
                </Document>
            </div>
            <div className='Learning_Book_Right'>
                <Document
                    file='/Hamlet.pdf'
                    onLoadSuccess={onDocumentLoadSuccess}
                >
                    {/* 왼쪽 페이지 다음 페이지 표시 */}
                    <Page pageNumber={pageNumber + 1} renderTextLayer={false}/>
                </Document>
            </div>
            <Button
                name='Learning_Button_Right'
                src='/Right.png'
                alt="Right navigation button"
                onClick={goToNextPage} // 오른쪽 버튼 클릭 시 두 페이지 증가
            />
        </>
    );
}

// Comprehension Component
function Comprehension({ onComprehensionComplete }) {
    return (
        <div className='Learning_Container'>
            <div className='LearningWon'>
                <ul>
                    <li className='Comprehension_problem'>안민용은 어떠한 사람인가요??</li>
                    <li className='Comprehension_Button_A' onClick={onComprehensionComplete}>병신</li>
                    <li className='Comprehension_Button_B'>착한사람</li>
                    <li className='Comprehension_Button_C'>봉사를 좋아하는 사람</li>
                </ul>
            </div>
        </div>
    );
}

function AI_Chat() {
// Treatment Component
}


function Treatment() {
    return (
        <div className='Learning_Container'>
            <div className='LearningWon'>

            </div>
        </div>
    );
}

// Main Component
export default function Learning() {
    const [showComprehension, setShowComprehension] = useState(false);
    const [showTreatment, setShowTreatment] = useState(false);

    const handleComprehensionTrigger = () => {
        setShowComprehension(true);
    };

    const handleComprehensionComplete = () => {
        setShowComprehension(false);
        setShowTreatment(true); // Treatment 페이지를 표시
    };

    return (
        <div>
            <a href="/Home"><button className='Home_Button'>Home</button></a>
            {showTreatment ? (
                <Treatment /> // Treatment 페이지를 표시
            ) : showComprehension ? (
                <Comprehension onComprehensionComplete={handleComprehensionComplete} />
            ) : (
                <Reading onComprehensionTrigger={handleComprehensionTrigger} />
            )}
        </div>
    );
}
