import React, { useState, useEffect } from 'react';
import * as pdfjsLib from 'pdfjs-dist/build/pdf';
import pdfWorker from 'pdfjs-dist/build/pdf.worker.entry';

// Setup PDF.js worker
pdfjsLib.GlobalWorkerOptions.workerSrc = pdfWorker;

function Button({ name, src, alt, onClick }) {
    return (
        <img src={src} className={name} alt={alt} onClick={onClick} />
    );
}

function Reading() {
    const [pdf, setPdf] = useState(null);
    const [currentPage, setCurrentPage] = useState(1);
    const [numPages, setNumPages] = useState(0);

    // PDF 로드
    useEffect(() => {
        const loadPdf = async () => {
            const loadingTask = pdfjsLib.getDocument('/your-pdf-file.pdf');
            const loadedPdf = await loadingTask.promise;
            setPdf(loadedPdf);
            setNumPages(loadedPdf.numPages);
        };
        loadPdf();
    }, []);

    const renderPage = async (pageNum, canvasId) => {
        if (!pdf) return;
        const page = await pdf.getPage(pageNum);
        const viewport = page.getViewport({ scale: 1.0 });
        const canvas = document.getElementById(canvasId);
        const context = canvas.getContext('2d');
        canvas.height = viewport.height;
        canvas.width = viewport.width;

        const renderContext = {
            canvasContext: context,
            viewport: viewport
        };
        page.render(renderContext);
    };

    const handlePrevPage = () => {
        if (currentPage > 1) {
            setCurrentPage(prevPage => prevPage - 2);
        }
    };

    const handleNextPage = () => {
        if (currentPage < numPages - 1) {
            setCurrentPage(prevPage => prevPage + 2);
        }
    };

    useEffect(() => {
        if (pdf) {
            renderPage(currentPage, 'canvasLeft');
            if (currentPage + 1 <= numPages) {
                renderPage(currentPage + 1, 'canvasRight');
            }
        }
    }, [pdf, currentPage]);

    return (
        <>
            <Button 
                name='Learning_Button_Left' 
                src='/Left.png' 
                alt="Left navigation button" 
                onClick={handlePrevPage} 
            />
            <div className='Learning_Book_Left'>
                <canvas id="canvasLeft"></canvas>
            </div>
            <div className='Learning_Book_Right'>
                <canvas id="canvasRight"></canvas>
            </div>
            <Button 
                name='Learning_Button_Right' 
                src='/Right.png' 
                alt="Right navigation button" 
                onClick={handleNextPage} 
            />
        </>
    );
}

export default Reading;
