import './App.css';
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import Turtle from './components/Turtle';
import Learning from './components/Learning';
import Bookshelf from './components/Bookshelf';
import Home from './components/Home';

// 공통적인 List 컴포넌트
function List(props) {
  return (
    <li className={props.thing}>
      <Link to={props.route}>{props.name}</Link>
    </li>
  );
}

// 로고 컴포넌트
function Logo() {
  return (
    <li >
     <a href='/Home'><img className='logo' src='/CIAE로고 2.png' alt='logo'/></a>
    </li>
  );
}

function App() {
  // 현재 경로 확인
  const location = useLocation();
  var Flag = false;

  if (location.pathname !== '/Learning') {
    Flag = true;
  } else if (location.pathname !== 'src/components/Login/Login.js') {
    Flag = true;
  } else {
    Flag = false;
  }

  if (Flag == false) { return (
    <Router>
      <div>
      {/* 특정 경로에서는 네비게이션 바 숨기기 */}
        <nav>
          <ul className='navAll'>
            <Logo />
            <List route='/Bookshelf' name='내 서재' thing='bookshelf' />
            <List route='/Turtle' name='거북이 수족관' thing='turtle' />
            <List route='/Learning' name='독서 시작하기' thing='learning' />
          </ul>
        </nav>

      {/* 페이지별 라우팅 설정 */}
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/Home" element={<Home />} />
        <Route path="/Bookshelf" element={<Bookshelf />} />
        <Route path="/Turtle" element={<Turtle />} />
        <Route path="/Learning" element={<Learning />} /> {/* Learning에서 네비게이션 바 숨김 */}
      </Routes>
    </div>
    </Router>
  );
} else {
  return 0
}

}

export default function AppWrapper() {
  return (
    <Router>
      <App />
    </Router>
  );
}
